import {useEffect} from "react";


export default function useGamePadPress(fn) {
    const hasGamepadAPI = () => "getGamepads" in navigator;
    const analogicType = {
        0: {
            id: "Left analogic",
            direction: {
                0: "left", //if value < 0
                1: "right" // if value > 0
            }
        },
        1: {
            id: "Left analogic",
            direction: {
                0: "up", //if value < 0
                1: "down" // if value > 0
            }
        },
        2: {
            id: "Right analogic",
            direction: {
                0: "left", //if value < 0
                1: "right" // if value > 0
            }
        },
        3: {
            id: "Right analogic",
            direction: {
                0: "up", //if value < 0
                1: "down" // if value > 0
            }
        }
    }
    let gamepadInput = null;

    useEffect(() =>{

            if(hasGamepadAPI()){
                window.addEventListener("gamepadconnected", function(e) {
                    const gamepadIndex = e.gamepad.index;
                    setInterval(() => {
                        if(gamepadIndex !== undefined) {
                            // a gamepad is connected and has an index
                            const myGamepad = navigator.getGamepads()[gamepadIndex];
                            if(myGamepad){
                                myGamepad.buttons.map(e => e.pressed).forEach((isPressed, buttonIndex) => {
                                    if(isPressed) {
                                        gamepadInput = {
                                            typeInput: "button",
                                            gamepad: myGamepad.id ,
                                            buttonPressed: buttonIndex
                                        }
                                        console.log(gamepadInput);
                                    }
                                })

                                myGamepad.axes.map((e , index) => {
                                    let tempvalue = e.toString()*100;
                                    if(tempvalue > 20 || tempvalue < -20){
                                        let direction = (tempvalue > 0)? analogicType[index].direction[1] : analogicType[index].direction[0];
                                        gamepadInput = {
                                            typeInput: "analogic",
                                            gamepad: myGamepad.id ,
                                            direction: direction,
                                            percentualeMovement: tempvalue
                                        }


                                        console.log(gamepadInput);
                                    }
                                    return null;
                                });
                            }

                        }
                    }, 100);
                });

                window.addEventListener("gamepaddisconnected", function (e) {
                    console.log(e);
                });

            }else{
                alert("No gamepad associated to your computer")
            }

        }
        , []);

}
