import React from "react";
import Sprite from "../sprite/Sprite";
import backgroundSprite from "../../game-assets/sprite/heroes/heroes1.png";


export default function Actor({sprite, data, step = 0, dir=0}){

    const { h, w } = data;

    return (
        <Sprite image={backgroundSprite} data={
            {
                x: step*w,
                y: dir*h,
                w,
                h
            }
        }
        />
    );

}
