import React, {Component} from "react";
import Player from "../game-tools/utils/player/player";
import useKeyPress from "../game-tools/utils/hooks/key-press/UseKeyPress";
import UseGamePad from "../game-tools/utils/hooks/gamepad-press/useGamePad";
import {GamepadsContext, GamepadsProvider} from "../game-tools/utils/hooks/gamepad-press/GamepadsContext";


function GameContainer() {

    const data = {
        h: 32,
        w: 32
    }



    useKeyPress((keybord) => {
       const dir = keybord.key.replace("Arrow", "").toLowerCase();
       console.log(dir);
    });


    return(
        <div style={{height: "600px", width: "600px", backgroundColor: "#000000"}}>
            <Player skin="heroes1" />

            <GamepadsProvider>
                <div className="App">
                    <GamepadsContext.Consumer>
                        {({ gamepads }) => {
                            // console.log(gamepads[0].buttons[3].pressed);
                            return (
                                <h3>
                                    Is the button pressed?:{" "}
                                    {gamepads &&
                                    gamepads.length > 0 &&
                                    gamepads.buttons.length > 0 &&
                                    gamepads[0].buttons[3].pressed
                                        ? "True"
                                        : "False"}
                                </h3>
                            );
                        }}
                    </GamepadsContext.Consumer>
                    <UseGamePad />
                </div>
            </GamepadsProvider>
        </div>
    );
}

export default GameContainer;
