import React from "react";

export default function Sprite({ image, data, position }) {

    const {y, x, h, w} = data;

    return(
        <div
            style={{
                position: "absolute",
                top: 50,
                left: position.x,
                display: "inline-block",
                height: `${h}px`,
                width:`${w}px`,
                backgroundImage: `url(${image})`,
                backgroundRepeat: "no-repeat",
                backgroundPosition: `-${x}px -${y}px`,
            }}

        />
    )
}
