    const data = {
        h: 32,
        w: 32
    }

    useKeyPress((e) => {
       const dir = e.key.replace("Arrow", "").toLowerCase();
    });


    return(
        <div style={{height: "600px", width: "600px", backgroundColor: "#000000"}}>
            <Player skin="heroes1" />
        </div>
    );
}

export default GameContainer;
