import React from "react";
import Actor from "../actor/actor";

function Player({ skin, step, dir }) {

    const data = {
        h: 36,
        w: 32
    }

    return(
        <div style={{height: "600px", width: "600px", backgroundColor: "#000000"}}>
            <Actor sprite={`/game-assets/sprite/heroes/${skin}.png`} data={data} step={step} dir={dir}/>
        </div>
    );
}

export default Player;
