import React, { useEffect, useRef, useState} from "react";

export default function useGamepads(fn) {

    useEffect(() =>{
            window.addEventListener(
                'gamepadconnected',
                function (e) {
                    gamepadHandler(e, true)
                },
                false
            )
            window.addEventListener(
                'gamepaddisconnected',
                function (e) {
                    gamepadHandler(e, false)
                },
                false
            )
        }
        , [fn])

}
