import { useEffect } from "react";


export default function useGamePadPress(fn) {

    useEffect(() =>{

            let gamepadIndex;

            window.addEventListener("gamepadconnected", function(e) {
                gamepadIndex = e.gamepad.index;
            });
        }
        , [fn]);

}
