const analogicType = {
    0: {
        id: "Left analogic",
        direction: {
            0: "left", //if value < 0
            1: "right" // if value > 0
        }
    },
    1: {
        id: "Left analogic",
        direction: {
            0: "up", //if value < 0
            1: "down" // if value > 0
        }
    },
    2: {
        id: "Right analogic",
        direction: {
            0: "left", //if value < 0
            1: "right" // if value > 0
        }
    },
    3: {
        id: "Right analogic",
        direction: {
            0: "up", //if value < 0
            1: "down" // if value > 0
        }
    }
}
let gamepadInput = null;


const gamepadEvent = (gamepad, callback) => {

    const gamepadIndex = gamepad.index;

    setInterval(() => {
        if(gamepadIndex !== undefined) {
            // a gamepad is connected and has an index
            const myGamepad = navigator.getGamepads()[gamepadIndex];
            if(myGamepad){
                myGamepad.buttons.map(e => e.pressed).forEach((isPressed, buttonIndex) => {
                    if(isPressed) {
                        gamepadInput = {
                            typeInput: "button",
                            gamepad: myGamepad.id ,
                            buttonPressed: buttonIndex
                        }
                    }
                    callback(gamepadInput);
                })

                myGamepad.axes.map((e , index) => {
                    let tempvalue = e.toString()*100;
                    if(tempvalue > 20 || tempvalue < -20){
                        let direction = (tempvalue > 0)? analogicType[index].direction[1] : analogicType[index].direction[0];
                        gamepadInput = {
                            typeInput: "analogic",
                            gamepad: myGamepad.id ,
                            direction: direction,
                            percentualeMovement: tempvalue
                        }
                        callback(gamepadInput);
                    }
                    return null;
                });
            }

        }
    }, 100);


}


function git(limit, callback) {
    var i = 0;
    var git = setInterval(function () {
        clearInterval(git);
        callback('done');
    }, 800);
}

git(5, function (x) {
    console.log(x);
});

export {gamepadEvent, gamepadInput, git}
